void InformFlavouredMarkdown__syntax_coloured_code(OUTPUT_STREAM, text_stream *text,
	text_stream *colouring, int from, int to, int mode) {
	inchar32_t current_col = 0;
	for (int i=from; i<to; i++) {
		inchar32_t c = Str__get_at(text, i);
		inchar32_t col = Str__get_at(colouring, i);
		if (col != current_col) {
			if (current_col) HTML_CLOSE("span");
			text_stream *span_class = NULL;
			switch (col) {
				case DEFINITION_COLOUR: span_class = TL_IS_2507; break;
				case FUNCTION_COLOUR:   span_class = TL_IS_2508; break;
				case RESERVED_COLOUR:   span_class = TL_IS_2509; break;
				case ELEMENT_COLOUR:    span_class = TL_IS_2510; break;
				case IDENTIFIER_COLOUR: span_class = TL_IS_2511; break;
				case HEADING_COLOUR:    span_class = TL_IS_2512; break;
				case CHARACTER_COLOUR:  span_class = TL_IS_2513; break;
				case CONSTANT_COLOUR:   span_class = TL_IS_2514; break;
				case STRING_COLOUR:     span_class = TL_IS_2515; break;
				case PLAIN_COLOUR:      span_class = TL_IS_2516; break;
				case EXTRACT_COLOUR:    span_class = TL_IS_2517; break;
				case COMMENT_COLOUR:    span_class = TL_IS_2518; break;
			}
			HTML_OPEN_WITH("span", "class=\"%S\"", span_class);
			current_col = col;
		}
		MDRenderer__char(OUT, c, mode);
	}
	if (current_col) HTML_CLOSE("span");
}

